import {
  Table,
  Radio,
  Form,
  Space,
  Button,
  Input,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  Image,
  TreeSelect,
  Switch,
} from "antd";
import { DownOutlined } from "@ant-design/icons";
import { useState } from "react";
import React from "react";
import { FormSocios } from "./FormSocios";
import { Modal } from "antd";
import Webcam from "react-webcam";

// const SizeType = Parameters<typeof Form>[0]['size'];

const columns = [
  {
    title: "Nombre",
    dataIndex: "name",
  },
  {
    title: "Cedula",
    dataIndex: "cedula",
  },
  {
    title: "Edad",
    dataIndex: "age",
    sorter: (a, b) => a.age - b.age,
  },
  {
    title: "Address",
    dataIndex: "address",
    filters: [
      {
        text: "London",
        value: "London",
      },
      {
        text: "New York",
        value: "New York",
      },
    ],
    onFilter: (value, record) => record.address.indexOf(value) === 0,
  },
  {
    title: "Acciones",
    key: "action",
    sorter: true,
    render: () => (
      <Space size="middle">
        <a>Editar</a>
        <a>Delete</a>
      </Space>
    ),
  },
];

const data = [];
for (let i = 1; i <= 10; i++) {
  data.push({
    key: i,
    name: "John Brown",
    cedula: "402-2348685-9",
    age: `${i}2`,
    address: `New York No. ${i} Lake Park`,
    description: `My name is John Brown, I am ${i}2 years old, living in New York No. ${i} Lake Park.`,
  });
}

const showHeader = true;
const footer = () => "Here is footer";
const pagination = { position: "bottom" };

export const TablaSocios = () => {
  console.log("Tabla");

  const [componentSize, setComponentSize] = "large";

  const [tomarFoto, setTomarFoto] = useState(false);

  const title = () => {
    return (
      <>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-around",
          }}
        >
          <Button type="primary" onClick={() => setModal2Visible(true)}>
            Agregar
          </Button>
          <span style={{ fontSize: "20px", fontWeight: "bold" }}>
            Listado de Socios
          </span>
        </div>
      </>
    );
  };

  const [state, setState] = useState({
    bordered: false,
    loading: false,
    pagination,
    size: "default",
    title: title,
    showHeader: true,
    footer: false,
    scroll: undefined,
    hasData: true,
    tableLayout: undefined,
    top: "none",
    bottom: "bottomLeft",
  });

  const handleToggle = (prop) => (enable) => {
    setState({ [prop]: enable });
  };

  const [state1, setState1] = useState({
    modal1Visible: false,
    modal2Visible: false,
  });

  const setModal2Visible = (modal2Visible) => {
    setState1({ modal2Visible });
  };

  const handleSizeChange = (e) => {
    setState({ size: e.target.value });
  };

  const handleTableLayoutChange = (e) => {
    setState({ tableLayout: e.target.value });
  };

  const handleEllipsisChange = (enable) => {
    setState({ ellipsis: enable });
  };

  const handleTitleChange = (enable) => {
    setState({ title: enable ? title : undefined });
  };

  const handleHeaderChange = (enable) => {
    setState({ showHeader: enable ? showHeader : false });
  };

  const handleFooterChange = (enable) => {
    setState({ footer: enable ? footer : undefined });
  };

  const handleRowSelectionChange = (enable) => {
    setState({ rowSelection: enable ? {} : undefined });
  };

  const handleYScrollChange = (enable) => {
    setState({ yScroll: enable });
  };

  const handleXScrollChange = (e) => {
    setState({ xScroll: e.target.value });
  };

  const handleDataChange = (hasData) => {
    setState({ hasData });
  };

  const webcamRef = React.useRef(null);
  const [imgSrc, setImgSrc] = React.useState("error");

  const capture = React.useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImgSrc(imageSrc);
    setTomarFoto(false);
  }, [webcamRef, setImgSrc]);

  const handleTomarFoto = () => {
    setTomarFoto(true);
  };

  const { xScroll, yScroll } = state;

  const scroll = {};
  if (yScroll) {
    scroll.y = 240;
  }
  if (xScroll) {
    scroll.x = "100vw";
  }

  const tableColumns = columns.map((item) => ({
    ...item,
    ellipsis: state.ellipsis,
  }));
  if (xScroll === "fixed") {
    tableColumns[0].fixed = true;
    tableColumns[tableColumns.length - 1].fixed = "right";
  }

  return (
    <>
      <Table
        {...state}
        pagination={{ position: [state.top, state.bottom] }}
        columns={tableColumns}
        dataSource={state.hasData ? data : null}
        scroll={scroll}
      />

      <Modal
        footer={false}
        title="Agregar Socio"
        centered
        visible={state1.modal2Visible}
        onOk={() => setModal2Visible(false)}
        onCancel={() => setModal2Visible(false)}
      >
        <Form
          labelCol={{ span: 4 }}
          wrapperCol={{ span: 14 }}
          layout="horizontal"
          initialValues={{ size: componentSize }}
          size={componentSize}
        >
          <Form.Item style={{ display: "flex"}}>
            <Image
              style={{ borderRadius: "10px" }}
              placeholder={true}
              width={100}
              src={imgSrc}
              fallback="data:image/png;base64,"
            />

            <Button
              onClick={handleTomarFoto}
              style={{ position: "absolute", top: "30px", right: "0px" }}
              type="primary"
            >
              Tomar Foto
            </Button>

            {tomarFoto && (
              <Button
                onClick={capture}
                style={{ position: "absolute", top: "30px", right: "0px" }}
                type="primary"
              >
                Guardar Foto
              </Button>
            )}

            <Image
              title={"Huella"}
              style={{ borderRadius: "10px" , marginLeft: "250px"}}
              placeholder={true}
              width={100}
              src={"error"}
              fallback="data:image/png;base64,"
            />
           
          </Form.Item>
          <Form.Item label="Nombre">
            <Input />
          </Form.Item>
          <Form.Item label="Apellido">
            <Input />
          </Form.Item>
          <Form.Item label="Nacimiento">
            <DatePicker />
          </Form.Item>

          <Form.Item label="Genero">
            <Select>
              <Select.Option value="masculino">Masculino</Select.Option>
              <Select.Option value="masculino">Femenino</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item label="Telefono">
            <Input />
          </Form.Item>
          <Form.Item label="Notificacion">
            <Switch />
          </Form.Item>

          <div
            style={{
              display: "flex",
              alignItems: "baseline",
              justifyContent: "space-around",
            }}
          >
            <Button type="primary">Agregar</Button>
            <Button type="ghost">Registrar Otra Huella</Button>
          </div>
        </Form>

        {tomarFoto && (
          <Webcam
            style={{ position: "absolute", top: "200px", left: "300px" }}
            height={220}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={280}
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
          />
        )}
      </Modal>
    </>
  );
};
